import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import { faMinus } from "@fortawesome/free-solid-svg-icons";
import { faBook } from "@fortawesome/free-solid-svg-icons"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { BrowserRouter as Router, Link, Route} from "react-router-dom";

class AddNewBook extends Component {
  render(){
    return (
      <form>
        <div className="form-group">
          <label htmlFor="formGroupExampleInput">Title</label>
          <input type="text" className="form-control" id="formGroupExampleInput" placeholder="Example input"></input>
        </div>
        <div className="form-group">
          <label htmlFor="formGroupExampleInput2">Authors</label>
          <input type="text" className="form-control" id="formGroupExampleInput2" placeholder="Another input"></input>
        </div>
      </form>
    );
  }
}


class MyBooks extends Component {
  constructor(props){
    super(props);
    this.state={
      books: []
    }
  }

  getBook(index){
    return this.state.books[index];
  }

  componentDidMount() {
    axios.get(`http://localhost:8080/books`)
      .then(res => {
        const books = res.data;
        this.setState({ books });
      });
  }

  handleDelete(event){
    const index = event.target.id;

  }



  render() {
    return (
      <div className="container">
        <div>
          <h1 className="header">My Books</h1>
        </div>
        <table className="table table-sm">
          <thead>
            <tr>
              <th>Title</th>
              <th>Authors</th>
              <th>Category</th>
              <th>Pages</th>
              <th>Read</th>
              <th>Year</th>
              <th></th>
            </tr>
        </thead>
        <tbody>
        {this.state.books.map(book =>
          <tr key={book.id}>
            <td>{book.title}</td>
            <td>{book.authors}</td>
            <td>{book.category}</td>
            <td>{book.numberOfPages}</td>
            <td>{book.readPages}/{book.numberOfPages}</td>
            <td>{book.yearOfPublishing}</td>
            <td><button onClick={this.handleDelete} className="btn btn-outline-secondary" id={book.id - 1}><FontAwesomeIcon icon={faMinus}/></button></td>
          </tr>
        )}
        </tbody>
        </table>


      </div>
    );
  }
}

class App extends Component {


  render() {
    return (
          <Router>
            <div className="App container">
              <nav className="navbar navbar-expand-lg navbar-light bg-light navigation">
                <Link className="navbar-brand" to={"/"}><FontAwesomeIcon icon={faBook} id="bookIcon"/><span id="brandname"> BookManager</span></Link>
                <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
                  <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
                  <div className="navbar-nav">
                    <Link className="nav-item nav-link active" to={"/"}>MyBooks <span className="sr-only">(current)</span></Link>
                    <Link className="nav-item nav-link active" to={"/wishlist"}>Whishlist</Link>
                    <Link className="nav-item nav-link active" to={"/add"}>Add New Book</Link>
                  </div>
                  <form className="form-inline my-2 my-lg-0">
                    <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search"></input>
                    <button className="btn btn-info my-2 my-sm-0" type="submit">Search</button>
                  </form>
                </div>
              </nav>

              <Route exact={true} path={"/"} render={() => (
                <div className="container booklist"><MyBooks /></div>
              )}/>
              <Route exact={true} path={"/add"} component={AddNewBook} />
            </div>
          </Router>
    );
  }
}

export default App;
