import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class MyBooks extends Component {
  constructor(props){
    super(props);
    this.state={
      books: null,
    };
  }



  render() {
    const Http = new XMLHttpRequest();
    const url = 'http://localhost:8080/books';
    var books;
    Http.open("GET", url);
    Http.send();
    Http.onreadystatechange = function(){
      books = JSON.parse(Http.responseText);
      setTimeout(() => {console.log("resources loading...")},3000);
    }

    this.setState({books:books});

    return(
    <div>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Authors</th>
            <th>Category</th>
            <th>Pages</th>
            <th>Read</th>
            <th>Year</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>{this.state.books[0].title}</td>
            <td>{this.state.books[0].authors}</td>
            <td>{this.state.books[0].category}</td>
            <td>{this.state.books[0].numberOfPages}</td>
            <td>{this.state.books[0].readPages}/{this.state.books[0].numberOfPages}</td>
            <td>{this.state.books[0].yearOfPublishing}</td>
          </tr>
        </tbody>
      </table>
    </div>
    );
  }
}

class App extends Component {
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
        <div><MyBooks /></div>
      </div>
    );
  }
}

export default App;
